        function ExpandInfo() {
            if ($("#divSaldo").is(":hidden")) {
                $("#divSaldo").slideDown("slow");
                $("#btnSaldoVis").addClass('btnSaldoVis');
                $("#btnSaldoVis").attr("title", "Hide account details");
