    function checkQuotaDaRaggiungere(oSrc, args) {
        var oTxtVincita = $get("hl_w_PC_ctl10_txtVincita");
        if (oTxtVincita == null) return;
        var oTxtGiocata = $get("hl_w_PC_ctl10_txtGiocata");
