        if ((!isNaN(parseFloat(oTxtGiocata.value.replace(",", ".")))) && (!isNaN(parseFloat(oTxtVincita.value.replace(",", "."))))) {
            if (((vincita / giocato) >= 10) && ((vincita / giocato) <= 20000 ))
